mapper.select:
select no, title, price, address, writedate, userid, content, username, status
from trade
where no = #{no}

mapper.update:
update trade
set title = #{updateParam.title},
    price = #{updateParam.price},
    content = #{updateParam.content},
    address = #{updateParam.address}
where no= #{no}

update trade
set status = #{status}
where no = #{no}
